syntax:
SELECT 학년, 이름, 연락처
FROM student
WHERE 학년 >= 3;

SELECT 학년, 이름, 연락처, 지도교수
FROM student
WHERE 학년 = 4 and 지도교수 = '일호준';

SELECT 학년, 이름, 연락처, 지도교수
FROM student
WHERE 학년 = 4 OR 지도교수 = '일호준';

SELECT 학년, 이름, 지도교수
FROM student
WHERE 지도교수 LIKE '%호준';

SELECT 학년, 이름, 연락처, 지도교수
FROM student
WHERE 지도교수 LIKE '김_';

SELECT 학번, 이름, 연락처, 지도교수
FROM student
WHERE 학번 >= 201900001 AND 학번 <= 201900030;

SELECT 학번, 국가장학금
FROM scholarship
WHERE 국가장학금 IS 2000000;
--WHERE 국가장학금 IS NOT NULL;

SELECT 학번, 이름, 지도교수
FROM student
WHERE 지도교수 IN ('일호준', '삼호준');
